import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from './services/auth.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  constructor(public authService: AuthService, private router: Router) {}

  get userProfilePhoto(): string | null {
    return this.authService.userProfilePhoto || null;
  }

  isLoggedIn(): boolean {
    return this.authService.isLoggedIn();
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<nav class="navbar bg-light mb-3">
  <div class="container d-flex align-items-center justify-content-between">
    <div></div>
    <!-- <span class="navbar-brand nav-btn mb-0 h1 mx-auto">OTP Authenticator App</span> -->
    <div *ngIf="isLoggedIn(); else guestLinks" class="dropdown">
      <img [src]="authService.userProfilePhoto || 'assets/default-avatar.png'" alt="Profile" class="profile-photo-navbar dropdown-toggle" id="profileDropdown" data-bs-toggle="dropdown" aria-expanded="false" style="width:40px; height:40px; border-radius:50%; cursor:pointer;">
      <ul class="dropdown-menu dropdown-menu-end" aria-labelledby="profileDropdown">
        <li><a class="dropdown-item" routerLink="/">Profile</a></li>
        <li><a class="dropdown-item" (click)="logout()">Logout</a></li>
      </ul>
    </div>
    <ng-template #guestLinks>
      <div>
        <!-- <button class="btn btn-primary me-2" routerLink="/">Login</button>
        <button class="btn btn-primary" routerLink="/register">Signup</button> -->
      </div>
    </ng-template>
  </div>
</nav>
<main class="container">
  <router-outlet></router-outlet>
</main>
