import { Component } from '@angular/core';

@Component({
  selector: 'app-otp-success',
  templateUrl: './otp-success.component.html',
  styleUrls: ['./otp-success.component.scss']
})
export class OtpSuccessComponent {}
<div class="otp-success-container">
  <div class="otp-success-card">
    <h2>Credentials Verified!</h2>
    <p>Your credentials  has been successfully verified. You are now logged in.</p>
    <a routerLink="/profile" class="btn btn-primary mt-3">Log Out</a>
  </div>
</div>
