import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, AbstractControl } from '@angular/forms';
import { mobileValidator } from '../validators';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent implements OnInit {
  registerForm: FormGroup;
  loading = false;
  error = '';
  success = '';
  submitted = false;

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private authService: AuthService
  ) {
    this.registerForm = this.formBuilder.group({
      'name': ['', Validators.required],
      'email': ['', [Validators.required, Validators.email]],
      'mobile': ['', [Validators.required, mobileValidator()]],
      'password': ['', [Validators.required, Validators.minLength(6)]],
      'confirmPassword': ['', Validators.required],
      'photo': [''] // profile photo is optional
    }, { validators: this.passwordsMatchValidator });
    // Listen for file input changes to convert to base64
    this.registerForm.get('photo')?.valueChanges.subscribe(file => {
      if (file && file instanceof File) {
        this.convertFileToBase64(file);
      }
    });

    this.registerForm.valueChanges.subscribe(() => {
      this.error = '';
      this.submitted = false;
    });
  }

  ngOnInit() {}

  get f(): { [key: string]: AbstractControl } {
    return this.registerForm.controls || {};
  }

  passwordsMatchValidator(form: FormGroup) {
    const password = form.get('password')?.value;
    const confirmPassword = form.get('confirmPassword')?.value;
    if (password !== confirmPassword) {
      form.get('confirmPassword')?.setErrors({ passwordMismatch: true });
    } else {
      form.get('confirmPassword')?.setErrors(null);
    }
    return null;
  }

  private getErrorMessage(error: any): string {
    if (error.error?.message) {
      return error.error.message;
    }
    if (error.status === 500) {
      return 'Server error occurred. Please try again later.';
    }
    if (error.status === 400) {
      return 'Invalid request. Please check your email.';
    }
    return 'An unexpected error occurred. Please try again.';
  }

  profilePhotoBase64: string = '';                            

  convertFileToBase64(file: File) {
    const reader = new FileReader();
    reader.onload = () => {
      this.profilePhotoBase64 = reader.result as string;
    };
    reader.readAsDataURL(file);
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    if (file) {
      this.convertFileToBase64(file);
      this.registerForm.patchValue({ photo: file });
    }
  }

  onSubmit() {
    this.submitted = true;

    if (this.registerForm.invalid) {
      this.error = 'Please fill in all required fields correctly.';
      return;
    }

    // Send JSON to match backend expectations
    const payload: any = {
      name: this.f['name'].value,
      email: this.f['email'].value,
      mobile: this.f['mobile'].value,
      password: this.f['password'].value,
      profilePhoto: this.profilePhotoBase64 || ''
    };
    this.loading = true;
    (async () => {
      try {
        const response = await this.authService.register(payload).toPromise();
        console.log('Registration response:', response);
        this.error = '';
        this.success = 'Registration successful! Please verify your email.';
        localStorage.setItem('pendingEmail', payload.email);
        localStorage.setItem('pendingMobile', payload.mobile);
        this.registerForm.reset();
        setTimeout(() => {
          this.router.navigate(['/verify-otp']);
        }, 1200);
      } catch (error) {
        console.error('Registration error:', error);
        this.success = '';
        this.error = this.getErrorMessage(error);
        this.loading = false;
      }
    })();
  }
}
<div class="container">
  <div class="card">
    <h2>Register</h2>
    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="name">Name</label>
        <input type="text" [formControlName]="'name'" class="form-control" id="name" placeholder="Enter your full name">
        <app-form-error [control]="f['name']"></app-form-error>
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input type="email" [formControlName]="'email'" class="form-control" id="email" placeholder="Enter your email">
        <app-form-error [control]="f['email']"></app-form-error>
      </div>

      <div class="form-group">
        <label for="mobile">Mobile No</label>
        <input type="text" [formControlName]="'mobile'" class="form-control" id="mobile" maxlength="10" placeholder="Enter your mobile number">
        <div *ngIf="f['mobile']?.touched && f['mobile']?.invalid" class="error">
          <div *ngIf="f['mobile']?.errors?.['required']">Mobile number is required</div>
          <div *ngIf="f['mobile']?.errors?.['mobileInvalid']">Invalid mobile number</div>
        </div>
      </div>

      <div class="form-group">
        <label for="password">Password</label>
        <input type="password" [formControlName]="'password'" class="form-control" id="password" autocomplete="new-password" placeholder="Enter your password">
        <div *ngIf="f['password']?.touched && f['password']?.invalid" class="error">
          <div *ngIf="f['password']?.errors?.['required']">Password is required</div>
          <div *ngIf="f['password']?.errors?.['minlength']">Password must be at least 6 characters</div>
        </div>
      </div>
      <div class="form-group">
        <label for="confirmPassword">Confirm Password</label>
        <input type="password" [formControlName]="'confirmPassword'" class="form-control" id="confirmPassword" autocomplete="new-password" placeholder="Re-enter your password">
        <div *ngIf="f['confirmPassword']?.touched && f['confirmPassword']?.invalid" class="error">
          <div *ngIf="f['confirmPassword']?.errors?.['required']">Confirm password is required</div>
          <div *ngIf="f['confirmPassword']?.errors?.['passwordMismatch']">Passwords do not match</div>
        </div>
      </div>

      <div class="form-group">
        <label for="photo">Profile Photo</label>
        <input type="file" class="form-control" id="photo" accept="image/*" (change)="onFileChange($event)" placeholder="Upload your profile photo">
        <app-form-error [control]="f['photo']"></app-form-error>
      </div>

      <button type="submit" [disabled]="loading" class="btn btn-primary">
        {{ loading ? 'Please wait...' : 'Register' }}
      </button>
    </form>

    <div *ngIf="error" class="error">{{ error }}</div>
  </div>
</div>
