import { Component, OnInit } from '@angular/core';
import { AuthService } from '../services/auth.service';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-otp-verified',
  templateUrl: './otp-verified.component.html',
  styleUrls: ['./otp-verified.component.scss']
})
export class OtpVerifiedComponent implements OnInit {
  constructor(
    public authService: AuthService,
    private userService: UserService
  ) {}

  ngOnInit() {
    this.userService.getProfile().subscribe(
      (response) => {
        if (response.user) {
          localStorage.setItem('currentUser', JSON.stringify(response.user));
          this.authService.currentUserSubject.next(response.user);
        }
      }
    );
  }
}
<div class="otp-verified-container">
  <div class="otp-verified-card">
    <img [src]="authService.userProfilePhoto || 'assets/default-avatar.png'" alt="Profile" class="profile-photo-navbar mb-3">
    <h2>Credentials Verified!</h2>
    <p class="mb-2">Your credentials has been successfully verified. You are now logged in.</p>
    <div *ngIf="authService.userProfilePhoto">
      <p class="text-success">This is the profile photo you registered with:</p>
      <img [src]="authService.userProfilePhoto" alt="Registered Profile" class="profile-photo-preview mb-3">
    </div>
    <a routerLink="/profile" class="btn btn-primary mt-3">Log Out</a>
  </div>
</div>
