import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-otp-verified',
  templateUrl: './otp-verified.component.html',
  styleUrls: ['./otp-verified.component.scss']
})
export class OtpVerifiedComponent {
  error: string = '';
  constructor(public authService: AuthService, private router: Router) {}

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }

  goToProfile() {
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('currentUser');
    if (!token || !user) {
      this.error = 'Session invalid or expired. Please log in again.';
      return;
    }
    this.error = '';
    this.router.navigate(['/profile']);
  }
}

<div class="otp-verified-container">
  <div class="otp-verified-card">

    <h2>Credentials Verified!</h2>
    <p class="mb-2">Your credentials has been successfully verified. You are now logged in.</p>

    <div *ngIf="error" class="alert alert-danger" role="alert" style="margin-bottom: 1rem;">
      <i class="fas fa-exclamation-circle"></i> {{ error }}
    </div>
    <div class="otp-verified-actions">
      <button (click)="logout()" class="btn btn-primary text-decoration-none">Log Out</button>
      <button (click)="goToProfile()" class="btn btn-primary text-decoration-none">Go to Profile</button>
    </div>
  </div>
</div>
